import React, {useState} from 'react'

function TodoForm({addTodo, updateTodo, edit}) {
    const[input, setInput] = useState('');
    const handleSubmit = (e) =>{
        e.preventDefault();
        if(!edit){
            addTodo({
                id: Math.floor(Math.random() * 1000),
                value: input,
                edit: false
            });
        }else{
            updateTodo({
                id:edit.id,
                value: edit.value,
                edit: false
            })
        }
        
    }
    return (
        <form className="todo-form" onSubmit={handleSubmit}>
            <input type="text" placeholder={edit? '' : 'Add Task'} value={input} name="task"  onChange={e => setInput(e.target.value)}/>
            <button className="todo-submit-btn">
                {edit ? 'Update': 'Add'}
            </button>
        </form>
    )
}

export default TodoForm
