import React, {useState} from 'react'
import {RiCloseCircleLine} from 'react-icons/ri';
import {TiEdit} from 'react-icons/ti';
import TodoForm from './TodoForm';

function Todo({passedTodo, deleteTodo}) {

    const [task, setTask] = useState (passedTodo.value)
    const [completed, setCompleted] = useState (passedTodo.completed)
    const [edit, setEdit] = useState (passedTodo.edit)

    if(edit) {
        return <TodoForm setTask={setTask} setEdit={setEdit} setComplete ={setCompleted}/>
    }
    return (
        <div className={completed ? 'todo-row-complete': 'todo-row'}>
            <p className="child" onClick={() => setCompleted(!completed)}> {task}</p>
            <RiCloseCircleLine className="child" onClick={() =>deleteTodo(passedTodo.id)}/>
            <TiEdit className="child" onClick={() => setEdit(true)}/>
        </div>
    )
}

export default Todo
