import React, {useState, useEffect} from 'react'
import {RiCloseCircleLine} from 'react-icons/ri';
import {TiEdit} from 'react-icons/ti';
import TodoForm from './TodoForm';

function Todo({todos, completeTodo, deleteTodo, updateTodo}) {

    const [editTodo, setEditTodo] = useState({
        id: null,
        value: '',
        edit: false,
        completed: null
    })

    const handleUpdate = () => {
        setEditTodo({
            id: null,
            value: '',
            edit: false,
            completed: null
        })
    }

    return todos.map((todo,index) => (editTodo.id === todo.id) ?
     (<TodoForm key={index} edit={editTodo} updateTodo={updateTodo} handleUpdate={handleUpdate}/>): (
        <div key={index} className={todo.completed ? 'todo-row complete': 'todo-row'}>
            <p className="" onClick={() => completeTodo(todo.id)}> {todo.value}</p>
            <RiCloseCircleLine className="child" onClick={() =>deleteTodo(todo.id)}/>
            <TiEdit className="child" onClick={() => setEditTodo(todo)}/>
        </div>
    ) ) 

}

export default Todo
