import React, {useState} from 'react'

function TodoForm() {
    const[input, setInput] = useState('');
    const handleSubmit = (e) =>{
        e.preventDefault();
        if(!props.edit){
            props.addTodo({
                id: Math.floor(Math.random() * 1000),
                value: input
            });
        }else{
            props.updateTodo({
                id:edit.id,
                value: edit.value
            })
        }
        
    }
    return (
        <form className="todo-form" onSubmit={handleSubmit}>
            <input type="text" placeholder={props.edit? '' : 'Add Task'} value={input} name="task"  onChange={e => setInput(e.target.value)}/>
            <button className="todo-submit-btn">
                {props.edit ? 'Update': 'Add'}
            </button>
        </form>
    )
}

export default TodoForm
