import React, {useState} from 'react'
import {RiCloseCircleLine} from 'react-icons/ri';
import {TiEdit} from 'react-icons/ti';

function Todo({todo, deleteTodo, edit}) {
    const [complete, setComplete] = useState(false)


    return (
        <div className={complete ? 'todo-row-complete': 'todo-row'}>
            <p className="child" onClick={e => {setComplete(!complete)}}> {todo.value}</p>
            <RiCloseCircleLine className="child" onClick={() =>deleteTodo(todo.id)}/>
            <TiEdit className="child" onClick={() =>editTodo(todo.id)}/>
        </div>
    )
}

export default Todo
