import React, {useState} from 'react'

function TodoForm({edit,addTodo, updateTodo, handleUpdate}) {
    const[input, setInput] = useState('');
    const handleSubmit = (e) =>{
        e.preventDefault();
        if(!edit){
            console.log(input);
            addTodo({
                id: Math.floor(Math.random() * 1000),
                value: input,
                edit: false,
                completed: false
            });
        }else{
            updateTodo({
                id: edit.id,
                value: input,
                edit: false,
                completed: edit.completed
            })
            handleUpdate()
        }
        
    }
    return (
        <form className="todo-form" onSubmit={handleSubmit}>
            <input className= {edit? 'todo-input edit' : 'todo-input'} type="text" placeholder={edit? '' : 'Add Task'} value={input} name="task" onChange={ e => setInput(e.target.value)}/>
            <button className="todo-submit-btn">
                {edit ? 'Update': 'Add'}
            </button>
        </form>
    )
}

export default TodoForm
