import React, {useState} from 'react';
import Todo from './Todo.js';
import TodoForm from './TodoForm';

function TodoList() {
    const [todos, setTodos] = useState([]);
    let list = []
    const addTodo = todo => {
        if (todo.value === '' || /^\s*$/.test(todo)) {
            return
        }

        const newTodos = [todo, ...todos];
        setTodos(newTodos);
        console.log(todo.id)
    }

    const updateTodo = newTodo => {

        const newTodos = todos.map(todo => (newTodo.id === todo.id ? newTodo : todo))
        setTodos(newTodos);
    }

    const deleteTodo = id => {
        console.log('deleting ', id, 'from', todos)
        const newTodos = todos.filter(todo => todo.id !== id);
        setTodos(newTodos)
        
    }

        

    return (
        <div>
            <h1>To-doooos List</h1>
            <TodoForm addTodo={addTodo}/>
            {todos.map((todo,index)=>< Todo key={index} todo={todo} deleteTodo={deleteTodo}/>)}
        </div>
    );
}

export default TodoList
