import React, {useState, useEffect, useRef} from 'react'

function TodoForm({edit,addTodo, updateTodo, handleUpdate}) {
    const[input, setInput] = useState('');

    const inputRef = useRef(null)

    useEffect(()=>{
        if(edit) setInput(edit.value);
        inputRef.current.focus();
    })
    const handleSubmit = (e) =>{
        e.preventDefault();
        if(!edit){
            console.log(input);
            addTodo({
                id: Math.floor(Math.random() * 1000),
                value: input,
                edit: false,
                completed: false
            });
        }else{
            updateTodo({
                id: edit.id,
                value: input,
                edit: false,
                completed: edit.completed
            })
            handleUpdate()
        }

        setInput('')
        
    }
    return (
        <form className=" w-10/12 m-3 ml-16" onSubmit={handleSubmit}>
            <input
            className= {edit ? 'todo-input edit' : 'todo-input outline-none p-1 leading-snug placeholder-gray-500 placeholder-opacity-50 border-2 border-solid border-green-600 border-opacity-75 rounded-md bg-green-200'}
            type="text" placeholder={edit? '' : 'add task..'} 
            value={input} name="task" 
            onChange={ e => setInput(e.target.value)}
            ref={inputRef}/>
            <button className="todo-submit-btn w-20">
                {edit ? 'Update': 'Add'}
            </button>
        </form>
    )
}

export default TodoForm
